import config from '../config/youtube';

class YouTubeService {
  constructor() {
    this.apiKey = config.youtube.apiKey;
    this.channelId = config.youtube.channelId;
    this.baseUrl = config.youtube.baseUrl;
  }

  // Verificar se API está disponível
  isApiAvailable() {
    return !!this.apiKey && this.apiKey.length > 0;
  }

  // Buscar transmissões ao vivo ATIVA
  async getLiveStreams() {
    if (!this.isApiAvailable()) {
      return this.getFallbackData();
    }

    try {
      console.log('🔴 Buscando transmissão ao vivo ATIVA...');
      const response = await fetch(
        `${this.baseUrl}/search?key=${this.apiKey}&channelId=${this.channelId}&eventType=live&type=video&part=snippet&maxResults=1`
      );
      
      if (!response.ok) throw new Error('Erro na API');
      const data = await response.json();
      console.log('📡 Transmissão ao vivo:', data);
      
      return data;
    } catch (error) {
      console.error('YouTube API Error:', error);
      return this.getFallbackData();
    }
  }

  // Buscar TRANSMISSÕES PASSADAS (que viram vídeos)
  async getRecentVideos(maxResults = 6) {
    if (!this.isApiAvailable()) {
      console.log('🔴 API não disponível - usando fallback');
      return this.getFallbackVideos();
    }

    try {
      console.log('🟡 Buscando TRANSMISSÕES do canal...');
      
      // Buscar TODOS os vídeos (incluindo transmissões passadas)
      const url = `${this.baseUrl}/search?key=${this.apiKey}&channelId=${this.channelId}&order=date&type=video&part=snippet&maxResults=${maxResults}`;
      console.log('📡 URL:', url);

      const response = await fetch(url);
      console.log('📊 Status:', response.status);
      
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}`);
      }
      
      const data = await response.json();
      console.log('📹 Transmissões encontradas:', data);
      
      if (data.error) {
        console.error('❌ Erro da API:', data.error);
        return this.getFallbackVideos();
      }

      if (data.items && data.items.length > 0) {
        console.log('✅ Transmissões encontradas:', data.items.length);
        
        // Filtrar apenas transmissões ou usar todas
        const transmissões = data.items.filter(item => 
          item.snippet.liveBroadcastContent === 'none' || // Transmissões finalizadas
          item.snippet.title.toLowerCase().includes('culto') ||
          item.snippet.title.toLowerCase().includes('live') ||
          item.snippet.title.toLowerCase().includes('transmissão')
        );
        
        console.log('🎬 Transmissões filtradas:', transmissões.length);
        
        if (transmissões.length > 0) {
          return await this.getVideoDetails(transmissões);
        } else {
          // Se não encontrou transmissões, usar todos os vídeos
          return await this.getVideoDetails(data.items);
        }
      } else {
        console.log('⚠️ Nenhuma transmissão encontrada');
        return this.getFallbackVideos();
      }
    } catch (error) {
      console.error('❌ Erro na requisição:', error);
      return this.getFallbackVideos();
    }
  }

  // Buscar detalhes dos vídeos/transmissões
  async getVideoDetails(videos) {
    if (!videos || videos.length === 0) return [];
    
    const videoIds = videos.map(item => item.id.videoId).join(',');
    
    try {
      const response = await fetch(
        `${this.baseUrl}/videos?key=${this.apiKey}&id=${videoIds}&part=contentDetails,statistics,snippet`
      );
      
      const detailsData = await response.json();
      
      return videos.map((item, index) => {
        const details = detailsData.items[index];
        return {
          id: item.id.videoId,
          titulo: item.snippet.title,
          data: new Date(item.snippet.publishedAt).toLocaleDateString('pt-BR'),
          descricao: item.snippet.description,
          thumbnail: item.snippet.thumbnails.medium.url,
          videoId: item.id.videoId,
          duracao: details?.contentDetails?.duration || 'PT0M0S',
          visualizacoes: details?.statistics?.viewCount || '0',
          // Informações específicas de transmissão
          isLive: item.snippet.liveBroadcastContent === 'live',
          wasLive: item.snippet.liveBroadcastContent === 'none' // Era uma live
        };
      });
    } catch (error) {
      console.error('Erro ao buscar detalhes:', error);
      return this.getFallbackVideos();
    }
  }

  // Fallback se API falhar
  getFallbackData() {
    return { items: [] };
  }

  getFallbackVideos() {
    // Seus vídeos de fallback (mantive os que você configurou)
    return [
